// Movement Calculator Node
const spyData = $input.item.json.SPY;
const currentPrice = spyData.latestTrade.p;
const openPrice = spyData.dailyBar.o;

// Calculate percentage change
const percentChange = ((currentPrice - openPrice) / openPrice) * 100;

// Store data in workflow static data for status tracking
$workflow.staticData.currentPrice = currentPrice;
$workflow.staticData.openingPrice = openPrice;
$workflow.staticData.percentChange = percentChange;
$workflow.staticData.lastPriceDate = new Date().toISOString();

// Determine signal based on movement
let signal = null;

if (percentChange >= 1.2) {
  signal = 'bearish_credit_spread'; // Market moved up significantly, sell call spreads
} else if (percentChange <= -1.2) {
  signal = 'bullish_credit_spread'; // Market moved down significantly, sell put spreads
}

// Check if we've already traded today
const today = new Date().toDateString();
const hasTraded = $workflow.staticData.lastTradeDate === today;

return [{
  json: {
    currentPrice,
    openPrice,
    percentChange: parseFloat(percentChange.toFixed(2)),
    signal,
    hasTraded,
    shouldTrade: signal !== null && !hasTraded,
    timestamp: new Date().toISOString()
  }
}];

// Account Validator Node
const movementData = $input.item.json;

// Only validate if we have a trading signal
if (!movementData.shouldTrade) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: 'No trading signal or already traded today',
      skipTrading: true
    }
  }];
}

// Check circuit breaker status
if ($workflow.staticData.circuitBreakerTripped) {
  const tripTime = new Date($workflow.staticData.circuitBreakerTime);
  const now = new Date();
  const hoursSinceTrigger = (now - tripTime) / (1000 * 60 * 60);

  // Reset circuit breaker after 4 hours
  if (hoursSinceTrigger > 4) {
    $workflow.staticData.circuitBreakerTripped = false;
    $workflow.staticData.circuitBreakerTime = null;
  } else {
    return [{
      json: {
        ...movementData,
        validationPassed: false,
        validationReason: `Circuit breaker active (${hoursSinceTrigger.toFixed(1)}h remaining)`,
        skipTrading: true
      }
    }];
  }
}

// Check market hours (basic validation)
const now = new Date();
const currentHour = now.getHours();
const currentMinutes = now.getMinutes();
const currentTime = currentHour * 100 + currentMinutes; // HHMM format

// Market hours: 9:30 AM - 4:00 PM ET (930 - 1600)
if (currentTime < 930 || currentTime >= 1600) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: 'Outside market hours',
      skipTrading: true
    }
  }];
}

// Validate movement threshold (additional check)
const absMovement = Math.abs(movementData.percentChange);
if (absMovement < 1.2) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Movement ${absMovement.toFixed(2)}% below 1.2% threshold`,
      skipTrading: true
    }
  }];
}

// Validate movement isn't too extreme (circuit breaker)
if (absMovement > 10) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Movement ${absMovement.toFixed(2)}% too extreme (>10%)`,
      skipTrading: true
    }
  }];
}

return [{
  json: {
    ...movementData,
    validationPassed: true,
    validationReason: 'All pre-checks passed',
    skipTrading: false
  }
}];

// Buying Power Validator Node
const accountData = $input.item.json;
const movementData = $node["Account Validator"].json;

// Skip if previous validation failed
if (!movementData.validationPassed) {
  return [{
    json: {
      ...movementData,
      accountData: accountData
    }
  }];
}

// Extract account information
const buyingPower = parseFloat(accountData.buying_power || 0);
const equity = parseFloat(accountData.equity || 0);
const dayTradingBuyingPower = parseFloat(accountData.daytrading_buying_power || 0);

// Check if options trading is enabled
const optionsTradingLevel = accountData.options_trading_level || 'none';
if (optionsTradingLevel === 'none' || optionsTradingLevel === null) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: 'Options trading not enabled on account',
      skipTrading: true,
      accountData: {
        buyingPower,
        equity,
        optionsTradingLevel
      }
    }
  }];
}

// Minimum equity requirement for options trading
if (equity < 2000) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Insufficient equity: $${equity.toFixed(2)} (minimum $2,000 required)`,
      skipTrading: true,
      accountData: {
        buyingPower,
        equity,
        optionsTradingLevel
      }
    }
  }];
}

// Estimate required buying power for credit spread
const estimatedMarginRequired = 100;
const availableBuyingPower = Math.min(buyingPower, dayTradingBuyingPower);

if (availableBuyingPower < estimatedMarginRequired) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Insufficient buying power: $${availableBuyingPower.toFixed(2)} (estimated $${estimatedMarginRequired} required)`,
      skipTrading: true,
      accountData: {
        buyingPower,
        equity,
        optionsTradingLevel,
        availableBuyingPower
      }
    }
  }];
}

// Check for reasonable position size relative to account
const maxRiskPerTrade = equity * 0.02; // 2% of equity max risk
if (estimatedMarginRequired > maxRiskPerTrade) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Risk too high: $${estimatedMarginRequired} exceeds 2% of equity ($${maxRiskPerTrade.toFixed(2)})`,
      skipTrading: true,
      accountData: {
        buyingPower,
        equity,
        optionsTradingLevel,
        availableBuyingPower,
        maxRiskPerTrade
      }
    }
  }];
}

return [{
  json: {
    ...movementData,
    validationPassed: true,
    validationReason: 'Account validation passed',
    skipTrading: false,
    accountData: {
      buyingPower,
      equity,
      optionsTradingLevel,
      availableBuyingPower,
      maxRiskPerTrade,
      estimatedMarginRequired
    }
  }
}];

// Enhanced Strike Selection with Validation
const optionsData = $input.item.json;
const validationData = $node["Buying Power Validator"].json;

// Skip if validation failed
if (!validationData.validationPassed) {
  return [{
    json: {
      ...validationData,
      strikeSelectionSkipped: true
    }
  }];
}

const currentPrice = validationData.currentPrice;
const signal = validationData.signal;
const maxRiskPerTrade = validationData.accountData.maxRiskPerTrade;

// Parse options chain
let optionsChain = [];
if (optionsData.SPY && optionsData.SPY.contracts) {
  optionsChain = Object.entries(optionsData.SPY.contracts).map(([symbol, data]) => ({
    symbol: symbol,
    type: symbol.includes('C') ? 'call' : 'put',
    strike_price: extractStrikeFromSymbol(symbol),
    last_quote: data.latest_quote || {},
    implied_volatility: data.greeks?.implied_volatility || 0,
    ...data
  }));
}

function extractStrikeFromSymbol(symbol) {
  const match = symbol.match(/[CP](\d{8})$/);
  return match ? (parseInt(match[1]) / 1000).toString() : null;
}

if (!optionsChain || optionsChain.length === 0) {
  return [{
    json: {
      ...validationData,
      validationPassed: false,
      validationReason: 'No options chain data available',
      skipTrading: true
    }
  }];
}

// Filter for same-day expiration with liquidity requirements
const today = new Date();
const todayStr = today.toISOString().substring(2, 10).replace(/-/g, '');

const todayOptions = optionsChain.filter(opt => {
  const symbolMatch = opt.symbol.match(/SPY(\d{6})[CP]/);
  const hasLiquidity = opt.last_quote.bid > 0.05 && 
                      opt.last_quote.ask > 0 && 
                      (opt.last_quote.ask - opt.last_quote.bid) <= opt.last_quote.bid * 0.5;

  return symbolMatch && symbolMatch[1] === todayStr && hasLiquidity;
});

if (todayOptions.length === 0) {
  return [{
    json: {
      ...validationData,
      validationPassed: false,
      validationReason: 'No liquid same-day options available',
      skipTrading: true
    }
  }];
}

// Find optimal strikes with risk validation
const atmStrike = Math.round(currentPrice * 2) / 2;
let shortStrike, longStrike, spreadType;

if (signal === 'bearish_credit_spread') {
  const calls = todayOptions.filter(opt => opt.type === 'call')
    .sort((a, b) => parseFloat(a.strike_price) - parseFloat(b.strike_price));

  shortStrike = calls.find(opt => parseFloat(opt.strike_price) >= atmStrike);
  if (shortStrike) {
    const spreadWidths = [0.5, 1.0, 2.0];
    for (const width of spreadWidths) {
      longStrike = calls.find(opt => 
        parseFloat(opt.strike_price) === parseFloat(shortStrike.strike_price) + width
      );
      if (longStrike) break;
    }
  }
  spreadType = 'call_credit_spread';

} else if (signal === 'bullish_credit_spread') {
  const puts = todayOptions.filter(opt => opt.type === 'put')
    .sort((a, b) => parseFloat(b.strike_price) - parseFloat(a.strike_price));

  shortStrike = puts.find(opt => parseFloat(opt.strike_price) <= atmStrike);
  if (shortStrike) {
    const spreadWidths = [0.5, 1.0, 2.0];
    for (const width of spreadWidths) {
      longStrike = puts.find(opt => 
        parseFloat(opt.strike_price) === parseFloat(shortStrike.strike_price) - width
      );
      if (longStrike) break;
    }
  }
  spreadType = 'put_credit_spread';
}

if (!shortStrike || !longStrike) {
  return [{
    json: {
      ...validationData,
      validationPassed: false,
      validationReason: `Unable to find suitable liquid strikes for ${signal}`,
      skipTrading: true
    }
  }];
}

// Calculate spread metrics with risk validation
const shortBid = parseFloat(shortStrike.last_quote.bid);
const shortAsk = parseFloat(shortStrike.last_quote.ask);
const longBid = parseFloat(longStrike.last_quote.bid);
const longAsk = parseFloat(longStrike.last_quote.ask);

const shortMidPrice = (shortBid + shortAsk) / 2;
const longMidPrice = (longBid + longAsk) / 2;
const netCredit = shortMidPrice - longMidPrice;
const strikeWidth = Math.abs(parseFloat(shortStrike.strike_price) - parseFloat(longStrike.strike_price));
const maxLoss = (strikeWidth * 100) - (netCredit * 100);

// Risk validation
if (maxLoss > maxRiskPerTrade) {
  return [{
    json: {
      ...validationData,
      validationPassed: false,
      validationReason: `Max loss $${maxLoss.toFixed(2)} exceeds risk limit $${maxRiskPerTrade.toFixed(2)}`,
      skipTrading: true
    }
  }];
}

// Credit validation
if (netCredit < 0.10) {
  return [{
    json: {
      ...validationData,
      validationPassed: false,
      validationReason: `Net credit $${netCredit.toFixed(2)} too low (minimum $0.10)`,
      skipTrading: true
    }
  }];
}

// Return on risk validation
const returnOnRisk = (netCredit * 100) / maxLoss;
if (returnOnRisk < 0.10) {
  return [{
    json: {
      ...validationData,
      validationPassed: false,
      validationReason: `Return on risk ${(returnOnRisk * 100).toFixed(1)}% too low (minimum 10%)`,
      skipTrading: true
    }
  }];
}

const limitPrice = Math.max(0.05, parseFloat((netCredit * 0.85).toFixed(2)));

return [{
  json: {
    ...validationData,
    signal,
    spreadType,
    shortStrike: {
      symbol: shortStrike.symbol,
      strike: shortStrike.strike_price,
      bid: shortBid,
      ask: shortAsk,
      midPrice: shortMidPrice.toFixed(2)
    },
    longStrike: {
      symbol: longStrike.symbol,
      strike: longStrike.strike_price,
      bid: longBid,
      ask: longAsk,
      midPrice: longMidPrice.toFixed(2)
    },
    limitPrice,
    netCredit: netCredit.toFixed(2),
    strikeWidth,
    maxLoss: maxLoss.toFixed(2),
    returnOnRisk: (returnOnRisk * 100).toFixed(1),
    atmStrike,
    currentPrice,
    validationPassed: true,
    validationReason: 'Strike selection completed with risk validation',
    timestamp: new Date().toISOString()
  }
}];

// Validation Failed Handler
const validationData = $input.item.json;

// Log the validation failure
const logEntry = {
  timestamp: new Date().toISOString(),
  reason: validationData.validationReason,
  currentPrice: validationData.currentPrice,
  percentChange: validationData.percentChange,
  signal: validationData.signal,
  accountData: validationData.accountData || 'Not available'
};

// Store validation failures for monitoring
if (!$workflow.staticData.validationFailures) {
  $workflow.staticData.validationFailures = [];
}
$workflow.staticData.validationFailures.push(logEntry);

// Keep only last 50 validation failures
if ($workflow.staticData.validationFailures.length > 50) {
  $workflow.staticData.validationFailures.splice(0, 
    $workflow.staticData.validationFailures.length - 50
  );
}

console.log(`Trading validation failed: ${validationData.validationReason}`);

return [{
  json: {
    success: false,
    action: 'validation_failed',
    reason: validationData.validationReason,
    logEntry,
    message: `Trading skipped due to validation failure: ${validationData.validationReason}`
  }
}];

// Process order response and update workflow state
const orderResponse = $input.item.json;
const strikeData = $node["Enhanced Strike Selection"].json;
const movementData = $node["Movement Calculator"].json;

// Mark that we've traded today
const today = new Date().toDateString();
$workflow.staticData.lastTradeDate = today;

// Store trade details
const tradeRecord = {
  orderId: orderResponse.id,
  symbol: 'SPY',
  spreadType: strikeData.spreadType,
  shortStrike: strikeData.shortStrike,
  longStrike: strikeData.longStrike,
  limitPrice: strikeData.limitPrice,
  maxLoss: strikeData.maxLoss,
  returnOnRisk: strikeData.returnOnRisk,
  triggerPrice: movementData.currentPrice,
  triggerMovement: movementData.percentChange,
  timestamp: new Date().toISOString(),
  status: orderResponse.status
};

// Add to trade history
const tradeHistory = $workflow.staticData.tradeHistory || [];
tradeHistory.push(tradeRecord);
$workflow.staticData.tradeHistory = tradeHistory;

return [{
  json: {
    success: true,
    orderId: orderResponse.id,
    orderStatus: orderResponse.status,
    tradeRecord,
    message: `${strikeData.spreadType} order placed successfully for ${strikeData.limitPrice} credit`
  }
}];

// Filter for today's SPY options positions
const positions = $input.item.json;
const today = new Date();
const todayStr = today.toISOString().substring(0, 10).replace(/-/g, '');

// Filter SPY options expiring today
const todayOptions = positions.filter(pos => {
  if (pos.asset_class !== 'us_option' || !pos.symbol.includes('SPY')) {
    return false;
  }

  // Extract expiration date from option symbol (YYMMDD format)
  const symbolMatch = pos.symbol.match(/SPY(\d{6})[CP]/);
  if (!symbolMatch) return false;

  const expirationStr = symbolMatch[1];
  // Convert YYMMDD to YYYYMMDD
  const fullYear = '20' + expirationStr.substring(0, 2);
  const fullDate = fullYear + expirationStr.substring(2);

  return fullDate === todayStr;
});

if (todayOptions.length === 0) {
  return [{
    json: {
      action: 'no_positions_to_close',
      message: 'No SPY options positions expiring today'
    }
  }];
}

// Create closing orders for each position
const closingOrders = todayOptions.map(position => {
  const qty = Math.abs(parseFloat(position.qty));
  const side = position.side === 'long' ? 'sell' : 'buy';
  const positionIntent = side === 'sell' ? 'sell_to_close' : 'buy_to_close';

  return {
    symbol: position.symbol,
    qty: qty.toString(),
    side: side,
    type: 'market',
    time_in_force: 'day',
    position_intent: positionIntent
  };
});

return closingOrders.map(order => ({ json: order }));

// Aggregate system status and trading statistics
if (!$workflow.staticData) {
  $workflow.staticData = {};
}

const systemStatus = {
  timestamp: new Date().toISOString(),
  system: {
    status: 'active',
    lastUpdate: new Date().toISOString(),
    circuitBreakerStatus: $workflow.staticData.circuitBreakerTripped || false
  },
  market: {
    currentPrice: $workflow.staticData.currentPrice || 'N/A',
    openingPrice: $workflow.staticData.openingPrice || 'N/A',
    percentChange: $workflow.staticData.percentChange || 0,
    lastPriceDate: $workflow.staticData.lastPriceDate || 'N/A'
  },
  trading: {
    lastTradeDate: $workflow.staticData.lastTradeDate || 'None',
    canTradeToday: $workflow.staticData.lastTradeDate !== new Date().toDateString()
  },
  statistics: {
    totalTrades: ($workflow.staticData.tradeHistory || []).length,
    recentTrades: ($workflow.staticData.tradeHistory || []).slice(-5),
    validationFailures: ($workflow.staticData.validationFailures || []).length,
    recentValidationFailures: ($workflow.staticData.validationFailures || []).slice(-5)
  },
  alerts: {
    errorCount: ($workflow.staticData.errorLog || []).length,
    lastError: ($workflow.staticData.errorLog || []).slice(-1)[0] || null
  }
};

return [{ json: systemStatus }];

// Error handler for trade execution
if (!$workflow.staticData) {
  $workflow.staticData = {};
}

const error = $input.item.json.error || $input.item.json;
const context = {
  node: $node,
  workflow: $workflow,
  timestamp: new Date().toISOString()
};

// Categorize error types
let errorType = 'unknown';
let recovery = 'none';
let severity = 'medium';

const errorMessage = (error.message || error.toString()).toLowerCase();

if (errorMessage.includes('insufficient buying power') || errorMessage.includes('insufficient funds')) {
  errorType = 'insufficient_funds';
  recovery = 'halt_trading';
  severity = 'high';
} else if (errorMessage.includes('rate limit') || errorMessage.includes('too many requests')) {
  errorType = 'rate_limit';
  recovery = 'exponential_backoff';
  severity = 'medium';
} else if (errorMessage.includes('market closed') || errorMessage.includes('not tradable')) {
  errorType = 'market_hours';
  recovery = 'wait_for_open';
  severity = 'low';
} else if (errorMessage.includes('connection') || errorMessage.includes('timeout')) {
  errorType = 'network';
  recovery = 'retry';
  severity = 'medium';
} else if (errorMessage.includes('strike') || errorMessage.includes('option')) {
  errorType = 'options_data';
  recovery = 'retry_with_delay';
  severity = 'medium';
}

// Create error log entry
const logEntry = {
  timestamp: new Date().toISOString(),
  errorType,
  severity,
  message: error.message || error.toString(),
  context: JSON.stringify(context, null, 2),
  recovery,
  stackTrace: error.stack || 'No stack trace available'
};

// Store in error log
if (!$workflow.staticData.errorLog) {
  $workflow.staticData.errorLog = [];
}
$workflow.staticData.errorLog.push(logEntry);

// Keep only last 100 errors
if ($workflow.staticData.errorLog.length > 100) {
  $workflow.staticData.errorLog.splice(0, $workflow.staticData.errorLog.length - 100);
}

// Implement circuit breaker
const recentErrors = $workflow.staticData.errorLog.filter(e => 
  new Date(e.timestamp) > new Date(Date.now() - 60 * 60 * 1000) // Last hour
);

if (recentErrors.length >= 5) {
  $workflow.staticData.circuitBreakerTripped = true;
  $workflow.staticData.circuitBreakerTime = new Date().toISOString();
}

return [{
  json: {
    errorHandled: true,
    errorType,
    severity,
    recovery,
    circuitBreakerStatus: $workflow.staticData.circuitBreakerTripped || false,
    recentErrorCount: recentErrors.length,
    logEntry
  }
}];
