// Extract the data field (which is a string) and parse it into a JSON object
const dataString = $input.first().json.data;

// Parse the data string into a valid JSON object
const dataObject = JSON.parse(dataString);

// Now you can safely extract the base64 image data
const base64Image = dataObject.base64_img;

// Decode the base64 string and create a buffer from it
const buffer = Buffer.from(base64Image, 'base64');

// You can optionally store this buffer in a temporary file or return the buffer as-is
return {
  json: {
    decoded_image_buffer: buffer.toString('base64'), // Returning as base64 for further processing
    image_length: buffer.length, // Optional: To verify the size of the decoded image
  }
};

const input = $input.first().json.output

const cleanedInput = input.replace(/```json|```/g, '').trim();

// Parse the cleaned JSON
const parsedOutput = JSON.parse(cleanedInput);

// Extract the `post_content.text` and `image_prompt.description` details
const postContent = parsedOutput.post_content.text;
const imagePrompt = parsedOutput.image_prompt.description;

// Prepare the final output in the desired JSON structure
return {
  post_content: postContent,
  image_prompt: imagePrompt
};
