// SDK Initialization and Configuration
const initAlpacaClient = () => {
  try {
    // Try to load official SDK
    const Alpaca = require('@alpacahq/alpaca-trade-api');

    console.log('‚úÖ Using official Alpaca SDK');
    return {
      client: new Alpaca({
        keyId: $credentials.alpaca.apiKeyId,
        secretKey: $credentials.alpaca.apiSecretKey,
        paper: true,
        feed: 'iex',
        usePolygon: false
      }),
      type: 'official_sdk'
    };
  } catch (sdkError) {
    console.log('‚ö†Ô∏è SDK not available, using enhanced HTTP client');

    // Enhanced HTTP client with SDK-like features
    const createEnhancedClient = () => {
      const baseUrl = 'https://paper-api.alpaca.markets';
      const dataUrl = 'https://data.alpaca.markets';

      const makeRequest = async (endpoint, options = {}) => {
        const url = `${options.dataApi ? dataUrl : baseUrl}${endpoint}`;

        const config = {
          method: options.method || 'GET',
          headers: {
            'APCA-API-KEY-ID': $credentials.alpaca.apiKeyId,
            'APCA-API-SECRET-KEY': $credentials.alpaca.apiSecretKey,
            'Content-Type': 'application/json',
            ...(options.headers || {})
          },
          ...(options.body && { body: JSON.stringify(options.body) })
        };

        let retries = 3;
        while (retries > 0) {
          try {
            const response = await fetch(url, config);

            if (!response.ok) {
              const errorData = await response.json().catch(() => ({}));

              // Rate limiting - retry with exponential backoff
              if (response.status === 429) {
                const delay = (4 - retries) * 1000;
                console.log(`Rate limited, retrying in ${delay}ms...`);
                await new Promise(resolve => setTimeout(resolve, delay));
                retries--;
                continue;
              }

              // Enhanced error messages
              let errorMessage = `Alpaca API Error ${response.status}`;
              if (errorData.message) {
                errorMessage += `: ${errorData.message}`;
              } else if (response.status === 403) {
                errorMessage += ': Insufficient permissions (check options trading level)';
              } else if (response.status === 422) {
                errorMessage += ': Invalid request parameters';
              }

              throw new Error(errorMessage);
            }

            return await response.json();
          } catch (error) {
            if (retries === 1 || !error.message.includes('429')) {
              throw error;
            }
            retries--;
          }
        }
      };

      return {
        // Account methods
        getAccount: () => makeRequest('/v2/account'),
        getPositions: () => makeRequest('/v2/positions'),

        // Market data methods  
        getSnapshots: (symbols) => makeRequest(
          `/v2/stocks/snapshots?symbols=${symbols.join(',')}&feed=iex`, 
          { dataApi: true }
        ),

        getOptionsChain: (symbol, params = {}) => {
          const query = new URLSearchParams(params).toString();
          return makeRequest(
            `/v1beta1/options/snapshots/${symbol}?${query}`, 
            { dataApi: true }
          );
        },

        // Trading methods
        createOrder: (orderData) => makeRequest('/v2/orders', {
          method: 'POST',
          body: orderData
        })
      };
    };

    return {
      client: createEnhancedClient(),
      type: 'enhanced_http'
    };
  }
};

// Initialize and test client
const alpacaSetup = initAlpacaClient();

// Store client globally for other nodes to use
$workflow.staticData.alpacaClient = alpacaSetup.client;
$workflow.staticData.clientType = alpacaSetup.type;

return [{
  json: {
    clientType: alpacaSetup.type,
    timestamp: new Date().toISOString(),
    status: 'initialized'
  }
}];

// Enhanced SPY Price Fetcher with SDK
const alpaca = $workflow.staticData.alpacaClient;

if (!alpaca) {
  throw new Error('Alpaca client not initialized. Run Initialize node first.');
}

try {
  let spyData;

  if ($workflow.staticData.clientType === 'official_sdk') {
    // Using official SDK
    const snapshots = await alpaca.getSnapshots(['SPY']);
    spyData = snapshots.SPY;
  } else {
    // Using enhanced HTTP client
    const snapshots = await alpaca.getSnapshots(['SPY']);
    spyData = snapshots.SPY;
  }

  // Validate data structure
  if (!spyData || !spyData.latestTrade || !spyData.dailyBar) {
    throw new Error('Invalid SPY data received - missing required fields');
  }

  const currentPrice = spyData.latestTrade.p || spyData.latestTrade.price;
  const openPrice = spyData.dailyBar.o || spyData.dailyBar.open;

  if (typeof currentPrice !== 'number' || typeof openPrice !== 'number') {
    throw new Error(`Invalid price data: current=${currentPrice}, open=${openPrice}`);
  }

  console.log(`‚úÖ SPY Data: Current=$${currentPrice}, Open=$${openPrice}`);

  return [{
    json: {
      SPY: {
        latestTrade: { p: currentPrice },
        dailyBar: { o: openPrice }
      },
      metadata: {
        timestamp: new Date().toISOString(),
        source: $workflow.staticData.clientType,
        priceValid: true
      }
    }
  }];

} catch (error) {
  const errorInfo = {
    timestamp: new Date().toISOString(),
    node: 'Fetch SPY Price',
    error: error.message,
    type: 'market_data_error',
    clientType: $workflow.staticData.clientType || 'unknown'
  };

  console.error('‚ùå SPY Price fetch failed:', errorInfo);

  // Store error for monitoring but don't stop workflow
  if (!$workflow.staticData.errorLog) {
    $workflow.staticData.errorLog = [];
  }
  $workflow.staticData.errorLog.push(errorInfo);

  throw new Error(`Failed to fetch SPY price: ${error.message}`);
}

// Movement Calculator with Enhanced Validation
const spyData = $input.item.json.SPY;

if (!spyData?.latestTrade?.p || !spyData?.dailyBar?.o) {
  throw new Error('Invalid SPY data structure received');
}

const currentPrice = spyData.latestTrade.p;
const openPrice = spyData.dailyBar.o;

// Validate price reasonableness (SPY typically $300-$600)
if (currentPrice < 200 || currentPrice > 800 || openPrice < 200 || openPrice > 800) {
  throw new Error(`Suspicious price data: current=${currentPrice}, open=${openPrice}`);
}

// Calculate percentage change
const percentChange = ((currentPrice - openPrice) / openPrice) * 100;

// Store data in workflow static data for status tracking
$workflow.staticData.currentPrice = currentPrice;
$workflow.staticData.openingPrice = openPrice;
$workflow.staticData.percentChange = percentChange;
$workflow.staticData.lastPriceDate = new Date().toISOString();

// Determine signal based on movement
let signal = null;
let signalStrength = 'none';

if (percentChange >= 1.2) {
  signal = 'bearish_credit_spread';
  signalStrength = percentChange >= 2.0 ? 'strong' : 'moderate';
} else if (percentChange <= -1.2) {
  signal = 'bullish_credit_spread';
  signalStrength = percentChange <= -2.0 ? 'strong' : 'moderate';
}

// Check if we've already traded today
const today = new Date().toDateString();
const hasTraded = $workflow.staticData.lastTradeDate === today;

// Enhanced trading conditions
const shouldTrade = signal !== null && !hasTraded && 
                   Math.abs(percentChange) <= 10; // Don't trade on extreme moves

console.log(`üìä Movement Analysis: ${percentChange.toFixed(2)}% | Signal: ${signal || 'none'} | Strength: ${signalStrength}`);

return [{
  json: {
    currentPrice,
    openPrice,
    percentChange: parseFloat(percentChange.toFixed(2)),
    absMovement: Math.abs(percentChange),
    signal,
    signalStrength,
    hasTraded,
    shouldTrade,
    tradingDay: today,
    timestamp: new Date().toISOString(),
    validation: {
      priceReasonable: true,
      movementValid: Math.abs(percentChange) <= 10,
      tradingEligible: shouldTrade
    }
  }
}];

// Enhanced Account Validator with SDK Integration
const movementData = $input.item.json;
const alpaca = $workflow.staticData.alpacaClient;

// Pre-validation checks
if (!movementData.shouldTrade) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `No trading conditions met: ${movementData.signal ? 'Already traded today' : 'Insufficient movement'}`,
      skipTrading: true
    }
  }];
}

// Check circuit breaker status
if ($workflow.staticData.circuitBreakerTripped) {
  const tripTime = new Date($workflow.staticData.circuitBreakerTime);
  const now = new Date();
  const hoursSinceTrigger = (now - tripTime) / (1000 * 60 * 60);

  // Reset circuit breaker after 4 hours
  if (hoursSinceTrigger > 4) {
    $workflow.staticData.circuitBreakerTripped = false;
    $workflow.staticData.circuitBreakerTime = null;
    console.log('üîì Circuit breaker reset after 4 hours');
  } else {
    return [{
      json: {
        ...movementData,
        validationPassed: false,
        validationReason: `Circuit breaker active (${hoursSinceTrigger.toFixed(1)}h remaining)`,
        skipTrading: true
      }
    }];
  }
}

// Enhanced market hours validation (consider timezone)
const now = new Date();
const etTime = new Date(now.toLocaleString("en-US", {timeZone: "America/New_York"}));
const currentHour = etTime.getHours();
const currentMinutes = etTime.getMinutes();
const currentTime = currentHour * 100 + currentMinutes;

// Market hours: 9:30 AM - 3:55 PM ET (leave 5 mins buffer before close)
if (currentTime < 930 || currentTime >= 1555) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Outside trading hours (${currentHour}:${currentMinutes.toString().padStart(2, '0')} ET)`,
      skipTrading: true,
      marketTime: {
        current: `${currentHour}:${currentMinutes.toString().padStart(2, '0')} ET`,
        marketOpen: '09:30 ET',
        tradingCutoff: '15:55 ET'
      }
    }
  }];
}

// Movement validation with enhanced checks
const absMovement = Math.abs(movementData.percentChange);
if (absMovement < 1.2) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Movement ${absMovement.toFixed(2)}% below 1.2% threshold`,
      skipTrading: true
    }
  }];
}

// Extreme movement check (potential data error or market disruption)
if (absMovement > 10) {
  return [{
    json: {
      ...movementData,
      validationPassed: false,
      validationReason: `Movement ${absMovement.toFixed(2)}% too extreme (>10% - possible data error)`,
      skipTrading: true
    }
  }];
}

// Check for market volatility (VIX proxy)
if (absMovement > 5) {
  console.log(`‚ö†Ô∏è High volatility detected: ${absMovement.toFixed(2)}% movement`);
}

console.log('‚úÖ Pre-validation checks passed');

return [{
  json: {
    ...movementData,
    validationPassed: true,
    validationReason: 'Pre-checks passed - proceeding to account validation',
    skipTrading: false,
    marketConditions: {
      volatilityAlert: absMovement > 5,
      signalStrength: movementData.signalStrength,
      marketTime: `${currentHour}:${currentMinutes.toString().padStart(2, '0')} ET`
    }
  }
}];

// Enhanced Account Info Fetcher with SDK
const alpaca = $workflow.staticData.alpacaClient;
const movementData = $input.item.json;

if (!alpaca) {
  throw new Error('Alpaca client not initialized');
}

// Skip if pre-validation failed
if (!movementData.validationPassed) {
  return [{ json: movementData }];
}

try {
  console.log('üìä Fetching account information...');

  const accountInfo = await alpaca.getAccount();

  // Validate required fields
  const requiredFields = ['buying_power', 'equity', 'daytrading_buying_power', 'status'];
  const missingFields = requiredFields.filter(field => !(field in accountInfo));

  if (missingFields.length > 0) {
    throw new Error(`Missing account fields: ${missingFields.join(', ')}`);
  }

  // Check account status
  if (accountInfo.status !== 'ACTIVE') {
    throw new Error(`Account not active: ${accountInfo.status}`);
  }

  // Parse numeric fields safely
  const buyingPower = parseFloat(accountInfo.buying_power || 0);
  const equity = parseFloat(accountInfo.equity || 0);
  const dayTradingBuyingPower = parseFloat(accountInfo.daytrading_buying_power || 0);

  // Enhanced account analysis
  const accountAnalysis = {
    ...accountInfo,
    computed: {
      available_buying_power: Math.min(buyingPower, dayTradingBuyingPower),
      max_risk_per_trade: equity * 0.02, // 2% max risk
      options_enabled: accountInfo.options_trading_level && 
                      accountInfo.options_trading_level !== 'none',
      account_health: equity > 2000 ? 'good' : 'insufficient',
      day_trades_remaining: parseInt(accountInfo.day_trade_count || 0) < 3 ? 
                           (3 - parseInt(accountInfo.day_trade_count || 0)) : 0
    },
    raw: {
      buying_power: buyingPower,
      equity: equity,
      daytrading_buying_power: dayTradingBuyingPower
    },
    metadata: {
      timestamp: new Date().toISOString(),
      source: $workflow.staticData.clientType
    }
  };

  console.log(`‚úÖ Account loaded: $${equity.toFixed(2)} equity, Options Level: ${accountInfo.options_trading_level}`);

  return [{ json: accountAnalysis }];

} catch (error) {
  const errorInfo = {
    timestamp: new Date().toISOString(),
    node: 'Get Account Info',
    error: error.message,
    type: 'account_data_error'
  };

  console.error('‚ùå Account info fetch failed:', errorInfo);

  // Store error but continue with fallback
  if (!$workflow.staticData.errorLog) {
    $workflow.staticData.errorLog = [];
  }
  $workflow.staticData.errorLog.push(errorInfo);

  throw new Error(`Failed to get account info: ${error.message}`);
}

// Validation Failed Handler
const validationData = $input.item.json;

// Create log entry
const logEntry = {
  timestamp: new Date().toISOString(),
  reason: validationData.validationReason,
  currentPrice: validationData.currentPrice,
  percentChange: validationData.percentChange,
  signal: validationData.signal,
  accountData: validationData.accountData || 'Not available'
};

// Store validation failures for monitoring
if (!$workflow.staticData.validationFailures) {
  $workflow.staticData.validationFailures = [];
}
$workflow.staticData.validationFailures.push(logEntry);

// Keep only last 50 validation failures
if ($workflow.staticData.validationFailures.length > 50) {
  $workflow.staticData.validationFailures.splice(0, 
    $workflow.staticData.validationFailures.length - 50
  );
}

console.log(`Trading validation failed: ${validationData.validationReason}`);

return [{
  json: {
    success: false,
    action: 'validation_failed',
    reason: validationData.validationReason,
    logEntry,
    message: `Trading skipped: ${validationData.validationReason}`
  }
}];

// Status Aggregator
if (!$workflow.staticData) {
  $workflow.staticData = {};
}

const systemStatus = {
  timestamp: new Date().toISOString(),
  system: {
    status: 'active',
    clientType: $workflow.staticData.clientType || 'unknown',
    circuitBreakerStatus: $workflow.staticData.circuitBreakerTripped || false
  },
  market: {
    currentPrice: $workflow.staticData.currentPrice || 'N/A',
    openingPrice: $workflow.staticData.openingPrice || 'N/A',
    percentChange: $workflow.staticData.percentChange || 0,
    lastPriceDate: $workflow.staticData.lastPriceDate || 'N/A'
  },
  trading: {
    lastTradeDate: $workflow.staticData.lastTradeDate || 'None',
    canTradeToday: $workflow.staticData.lastTradeDate !== new Date().toDateString()
  },
  statistics: {
    totalTrades: ($workflow.staticData.tradeHistory || []).length,
    validationFailures: ($workflow.staticData.validationFailures || []).length,
    errorCount: ($workflow.staticData.errorLog || []).length
  }
};

return [{ json: systemStatus }];
