const charges = items.map(item => item.json);
const summary = charges.reduce((acc, charge) => {
  const date = new Date(charge.created * 1000).toISOString().split("T")[0];
  acc[date] = (acc[date] || 0) + charge.amount / 100;
  return acc;
}, {});
return [{ json: { summary } }];

